mapper.select:
select simid, simtype, command, simserver, price, applytype, date_format(modifytime, '%Y-%m-%d %T') modifytime from siminfo where
simtype = #{simType} and
command like concat('%', #{command}, '%') and price like concat('%', #{price}, '%') order by modifytime desc limit #{startIndex}, #{pageSize}

mapper.update:
update siminfo set command =
#{command}, simserver = #{simServer}, price = #{price} where simid =
#{simId}
