mapper.select:
select o.orderno, o.prop, o.price, o.state, o.sms, o.reply, g.gamename, p.payname, o.modifytime from orderinfo o, pay_order po, payinfo p, gameinfo g, cp_game cg where o.orderno = po.orderno and po.payid = p.payid and o.gameid = g.gameid and g.gameid = cg.gameid
and cg.cpid = #{cpId} and o.reply != '2'
and o.orderno like concat('%', #{orderNo}, '%')
and o.gameid = #{gameId}
and o.modifytime >= #{startTime}
and o.modifytime <= concat(#{endTime}, ' 23:59:59')
order by o.modifytime desc limit #{startIndex}, #{pageSize}

select count(o.orderno) from orderinfo o, cp_game cg where o.gameid = cg.gameid
and cg.cpid = #{cpId} and o.reply != '2'
and o.orderno like concat('%', #{orderNo}, '%')
and o.gameid = #{gameId}
and o.modifytime >= #{startTime}
and o.modifytime <= concat(#{endTime}, ' 23:59:59')

mapper.update:
update orderinfo set
state
= 1, modifytime = #{modifyTime} where orderno = #{orderNo}

update orderinfo set
reply
= #{reply} where orderno = #{orderNo}
